create table staff
(
    name        varchar(10)  not null
        primary key,
    dep_name    varchar(100) null comment '部门',
    att_day     int          null comment '应出勤天数',
    late        int          null comment '迟到次数',
    act_att_day int          null comment '实际出勤天数',
    leave_early int          null comment '早退',
    on_miss     int          null comment '上班缺卡',
    off_miss    int          null comment '下班缺卡',
    no_work     int          null comment '旷工'
);

